         title(paste("Samples of size", n))  for ( i in (1:B)){     samplex=rnorm(n,mean,sd)     meanx[i]=mean(samplex)     stdex[i]=sqrt(var(samplex)/n)     lower[i]=meanx[i]-1.96*stdex[i]     upper[i]=meanx[i]+1.96*stdex[i]     lines(c(lower[i],upper[i]),c(i,i))}   lines(c(mean,mean),c(0,B+1)) } cis(n=15,B=50)